<?php include("datosConexion.php");include("reproduccion.php");$conexion=mysql_connect($host,$usuario,$contrasena)or die("problemas al conectar con el servidor ".mysql_error());mysql_select_db($bd,$conexion)or die("problemas al conectar con la base de datos ".mysql_error());function obtenerCantidadVotantes($idj){$cantidad=mysql_query("SELECT CANTIDADVOTANTES FROM puntuacionjuegos WHERE IDJUEGO='$idj'")or die("Error al obtener la cantidad de votantes de este juego ".mysql_error());return mysql_result($cantidad,0);}function obtenerSumatoriaVotaciones($idj){$total=mysql_query("SELECT SUMATORIAVOTOS FROM puntuacionjuegos WHERE IDJUEGO='$idj'")or die("Error al obtener la totalidad de la suma de los votantos ".mysql_error());return mysql_result($total,0);}function mostrarPuntuacion($idj){echo $puntuacion=obtenerPuntuacion($idj);if($puntuacion==10)echo ".0";elseif($puntuacion==floor($puntuacion))echo ".00";}function obtenerPuntuacion($idj){if(obtenerCantidadVotantes($idj)!=0)return round(obtenerSumatoriaVotaciones($idj)/obtenerCantidadVotantes($idj)*100)/100;else return 0;}?>