<?php include("obtencionDatosBD.php");$ip=obtenerIPUsuario();$voto=$_POST['voto'];$id=$_POST['idj'];$query=mysql_query("SELECT IDJUEGO FROM votantesjuegos WHERE IPUSUARIO='$ip' AND IDJUEGO='$id'");if(mysql_num_rows($query)==1)echo 0;else{if(strcmp($voto,"http://www.jaexjuegos.com/imagen/dislike.png")==0){mysql_query("UPDATE puntuacionjuegos SET CANTIDADVOTANTES=CANTIDADVOTANTES+1 WHERE IDJUEGO='$id'")or die("Error al actualizar la cantidad de votos negativos de este juego ".mysql_error());}else{mysql_query("UPDATE puntuacionjuegos SET SUMATORIAVOTOS=SUMATORIAVOTOS+1 WHERE IDJUEGO='$id'")or die("Error al actualizar la cantidad de votos positivos de este juego ".mysql_error());}mysql_query("INSERT INTO votantesjuegos(IDJUEGO, IPUSUARIO, FECHA)VALUES('$id','$ip',NOW())",$conexion)or die("Error al registrar la ip del votante");echo 1;}?>